SELECT pha.po_header_id ,pha.segment1 协议编号 ,pha.comments 协议摘要 ,hou.name 组织名称 ,he.full_name 采购员 ,pha.attribute6 税码 ,flv.meaning 协议类型,pha.attribute3 ,pha.attribute4 ,pha.attribute2 ,pha.attribute8 合同名称 ,pha.blanket_total_amount 含税总金额-- ,pha.segment2 主合同编号,pha2.segment1--   ,round(pha.blanket_total_amount /-- (1 + regexp_substr(pha.attribute6--    ,'[0-9]+') / 100)--    ,2) 不含税总金额      ,pha.currency_code 币种      ,pha.attribute7 交货周期      ,plc.meaning authorization_status_dsp      ,to_char(pha.start_date, 'YYYY-MM-DD') 开始时间      ,to_char(pha.end_date,'YYYY-MM-DD') 结束时间       ,pv.segment1 供应商编码       ,pv.vendor_name 供应商名称       ,to_char(pha.creation_date,'YYYY-MM-DD') order_dateFROM   po_headers_all     pha      ,po_headers_all     pha2      ,hr_operating_units hou      ,hr_employees he      ,hr_locations_all hla      ,fnd_lookup_values_vl plc      ,po_vendors pv      ,fnd_lookup_values_vl flvWHERE  hou.organization_id(+) = pha.attribute11AND    pha.segment2 = pha2.po_header_id(+)AND    he.employee_id = pha.agent_idAND    flv.lookup_type = 'CUX_PO_DOCUMENTS_TYPE'AND    flv.lookup_code = pha.type_lookup_codeAND    hla.location_id = pha.ship_to_location_idAND    plc.lookup_type = 'CUX_DOCUMENTS_STATUS'AND    pha.enabled_flag = 'Y'AND    plc.lookup_code = (CASEWHEN pha.authorization_status IN ('APPROVING','APPROVED','REJECTED')  THEN pha.authorization_statusELSE  'DRAFT'  END) AND    pha.authorization_status = 'APPROVED'AND    nvl(pha.cancel_flag,'N') = 'N'AND    pv.vendor_id = pha.vendor_idAND    pha.type_lookup_code = 'BLANKET'